<?phpadd_action('init', 'classmates_register');function classmates_register() {		$labels = array(		'name' => _x('Classmates', 'post type general name'),		'singular_name' => _x('Classmates', 'post type singular name'),		'add_new' => _x('Add New', 'classmates'),		'add_new_item' => __('Add New Classmate'),		'edit_item' => __('Edit Classmate'),		'new_item' => __('New Classmate'),		'view_item' => __('View Classmate'),		'search_items' => __('Search Classmates'),		'not_found' =>  __('Nothing found'),		'not_found_in_trash' => __('Nothing found in Trash'),		'parent_item_colon' => ''	);		$args = array(		'labels' => $labels,		'public' => true,		'publicly_queryable' => true,		'show_ui' => true,		'query_var' => true,		'rewrite' => array('slug' => 'classmates'),		'capability_type' => 'post',		'hierarchical' => false,		'menu_position' => null,		'supports' => array('title','editor','author','page-attributes'),		'has_archive' => true,	);		register_post_type( 'classmates' , $args );	}