<?php// Custom Post Typeadd_action('init', 'dailystories_register');function dailystories_register() {		$labels = array(		'name' => _x('Daily Stories', 'post type general name'),		'singular_name' => _x('Daily Story', 'post type singular name'),		'add_new' => _x('Add New', 'dailystory'),		'add_new_item' => __('Add New Daily Story'),		'edit_item' => __('Edit Daily Story'),		'new_item' => __('New Daily Story'),		'view_item' => __('View Daily Story'),		'search_items' => __('Search Daily Story'),		'not_found' =>  __('Nothing found'),		'not_found_in_trash' => __('Nothing found in Trash'),		'parent_item_colon' => ''	);		$args = array(		'labels' => $labels,		'public' => true,		'publicly_queryable' => true,		'show_ui' => true,		'query_var' => true,		'rewrite' => array('slug' => 'daily-stories'),		'capability_type' => 'post',		'hierarchical' => false,		'menu_position' => null,		'supports' => array('title','editor','author','page-attributes','comments'),		'has_archive' => true,		'show_in_rest' => true,	);		register_post_type( 'dailystories' , $args );	}