<?phpadd_action('init', 'flow_register');function flow_register() {		$labels = array(		'name' => _x('Flow of the Day', 'post type general name'),		'singular_name' => _x('Flow of the Day', 'post type singular name'),		'add_new' => _x('Add New', 'flow'),		'add_new_item' => __('Add New Entry'),		'edit_item' => __('Edit Entry'),		'new_item' => __('New Entry'),		'view_item' => __('View Entry'),		'search_items' => __('Search Entry'),		'not_found' =>  __('Nothing found'),		'not_found_in_trash' => __('Nothing found in Trash'),		'parent_item_colon' => ''	);		$args = array(		'labels' => $labels,		'public' => true,		'publicly_queryable' => true,		'show_ui' => true,		'query_var' => true,		'rewrite' => array('slug' => 'flow'),		'capability_type' => 'post',		'hierarchical' => false,		'menu_position' => null,		'supports' => array('title','editor','author','page-attributes','comments'),		'has_archive' => true,		'show_in_rest' => true,	);		register_post_type( 'flow' , $args );	}