<?phpadd_action('init', 'weeklyplan_register');function weeklyplan_register() {		$labels = array(		'name' => _x('Weekly Plan', 'post type general name'),		'singular_name' => _x('Weekly Plan', 'post type singular name'),		'add_new' => _x('Add New', 'weeklyplan'),		'add_new_item' => __('Add New Weekly Plan'),		'edit_item' => __('Edit Weekly Plan'),		'new_item' => __('New Weekly Plan'),		'view_item' => __('View Weekly Plan'),		'search_items' => __('Search Weekly Plan'),		'not_found' =>  __('Nothing found'),		'not_found_in_trash' => __('Nothing found in Trash'),		'parent_item_colon' => ''	);		$args = array(		'labels' => $labels,		'public' => true,		'publicly_queryable' => true,		'show_ui' => true,		'query_var' => true,		'rewrite' => array('slug' => 'weekly-plan'),		'capability_type' => 'post',		'hierarchical' => false,		'menu_position' => null,		'supports' => array('title','editor','author','page-attributes','comments'),		'has_archive' => true,		'show_in_rest' => true,	);		register_post_type( 'weeklyplan' , $args );	}